  const successMessage = () => {
    if (success) {
      return <h4 className="text-success">Skill created successfully</h4>;
    }
  };

  const warningMessage = () => {
    if (error) {
      return <h4 className="text-success">Failed to create Skill</h4>;
    }
  };
*/}
  const myCategoryForm = () => (
    <div className="wrapper">

    <div className="form-wrapper">
      <span><Link to = "/admin/dashboard">Admin H</Link></span>
      <h1>ADD SKILL</h1>
      <form>
        <div className="name">
          <label htmlFor="name">Skill's Name</label>
          <input
            placeholder="name"
            type="name"
            name="name"
            onChange={handleChange("name")}
            value={name}
          />

        </div>
        <div className="stats">
          <label htmlFor="stats">Add Stats</label>
          <input
             onChange={handleChange("stats")}
            value={stats}
            placeholder="stats"
            type="stats"
            name="stats"

          />

        </div>
        <div className="createAccount">
          <button type="submit" onClick={onSubmit} >Login</button>



        </div>
      </form>
    </div>
  </div>


    );


  const successMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-success"
            style={{ display: success ? "" : "none" }}
          >
            Created Successfully
          </div>
        </div>
      </div>
    );
  };

  const errorMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-danger"
            style={{ display: error ? "" : "none" }}
          >
            {error}
          </div>
        </div>
      </div>
    );
  };

  return (
    <
    >

        {successMessage()}
      {errorMessage()}
          {myCategoryForm()}
          {goBack()}

    </>
  );
};

export default AddSkill;
