import React, { useState , useEffect } from "react";
import Base from "../core/Base";
import { isAutheticated } from "../auth/helper";
import { Link } from "react-router-dom";
import { createEducation ,  getSkills } from "./helper/adminapicall";

const AddEducation = () => {
  const { user, token } = isAutheticated();

  const [values, setValues] = useState({
    name: "",
    board: "",
    year: "",
    photo: "",
    qualification: "",
    loading: false,
    error: "",
    createdEducation: "",
    getaRedirect: false,
    formData: ""
  });

  const {
    name,
    board,
    year,
    qualification,
    loading,
    error,
    photo,
    createdEducation,
    getaRedirect,
    formData
  } = values;

  const preload = () => {
    getSkills().then(data => {
      //console.log(data);
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setValues({ ...values, skills: data, formData: new FormData() });
      }
    });
  };

  useEffect(() => {
    preload();
  }, []);

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: "", loading: true });
    createEducation(user._id, token, formData).then(data => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setValues({
          ...values,
          name: "",
        board: "",
          qualification: "",
          photo: "",
          year: "",
          loading: false,
          createdEducation: data.name
        });
      }
    });
  };

  const handleChange = name => event => {
    const value = name === "photo" ? event.target.files[0] : event.target.value;
    formData.set(name, value);
    setValues({ ...values, [name]: value });
  };

  const successMessage = () => (
    <div
      className="alert alert-success mt-3"
      style={{ display: createdEducation ? "" : "none" }}
    >
      <h4>{createdEducation} created successfully</h4>
    </div>
  );

  const createEducationForm = () => (
    <form>
       <div className="form-group">
        <label className="btn btn-block btn-success">
          <input
            onChange={handleChange("photo")}
            type="file"
            name="photo"
            accept="image"
            placeholder="choose a file"
          />
        </label>
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("name")}
          name="photo"
          className="form-control"
          placeholder="Name"
          value={name}
        />
      </div>
      <div className="form-group">
        <textarea
          onChange={handleChange("year")}
          name="photo"
          className="form-control"
          placeholder="Description"
          value={year}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("qualification")}
          type="text"
          className="form-control"
          placeholder="tech_stack"
          value={qualification}
        />
      </div>
     {/* <div className="form-group">
        <select
          onChange={handleChange("skill")}
          className="form-control"
          placeholder="skill"
        >
          <option>Select</option>
          {skills &&
            skills.map((cate, index) => (
              <option key={index} value={cate._id}>
                {cate.name}
              </option>
            ))}
        </select>
            </div> */}
      <div className="form-group">
        <input
          onChange={handleChange("board")}
          type="text"
          className="form-control"
          placeholder="github_link"
          value={board}
        />
      </div>
      
      <button
        type="submit"
        onClick={onSubmit}
        className="btn btn-outline-success mb-3"
      >
        Create Education
      </button>
    </form>
  );

  return (
    <Base
      title="Add a Education here!"
      description="Welcome to Education creation section"
      className="container bg-info p-4"
    >
      <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-3">
        Admin Home
      </Link>
      <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {createEducationForm()}
        </div>
      </div>
    </Base>
  );
};

export default AddEducation;
