import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { getskills, createaProject} from "./helper/adminapicall";
import { isAutheticated } from "../auth/helper/index";

const AddProject = () => {
  const { user, token } = isAutheticated();

  const [values, setvalues] = useState({
    name: "",
    description: "",
    project_link: "",
    github_link: "",
    photo: "",
    tech_stack: [],
    loading: false,
    error: "",
    createdProject: "",
    getaRedirect: false,
    formData: ""
  });

  const {
    name,
    description,
    project_link,
     github_link,
     tech_stack,
    loading,
    error,
    createdProject,
    getaRedirect,
    formData
  } = values;

 

  const onSubmit = (event) => {
    event.preventDefault();
    setvalues({ ...values, error: "", loading: true });
    createaProject(user._id, token, formData).then(data => {
      if (data.error) {
        setvalues({ ...values, error: data.error });
      } else {
        setvalues({
          ...values,
          name: "",
          description: "",
          project_link : "",
          github_link : "",
      tech_stack : "",
          photo: "",
          stock: "",
          loading: false,
          createdProject: data.name
        });
      }
    });
  };
  const handleChange = name => event => {
    const value = name === "photo" ? event.target.files[0] : event.target.value
    formData(name, value);
    setvalues({...values, [name]: value})
}

  const successMessage = () => (
    <div
      className="alert alert-success mt-3"
      style={{ display: createdProject? "" : "none" }}
    >
      <h4>{createdProject} created successfully</h4>
    </div>
  );

  const createProjectForm = () => (
    <form>
         <span>Post photo</span>
          <div className="form-group">
            <label className="btn btn-block btn-success">
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
      <div className="form-group">
        <input
          onChange={handleChange("name")}
          name="photo"
          className="form-control"
          placeholder="Name"
          value={name}
        />
      </div>
      <div className="form-group">
        <textarea
          onChange={handleChange("description")}
          name="photo"
          className="form-control"
          placeholder="Description"
          value={description}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("project_link")}
          type="number"
          className="form-control"
          placeholder="Project"
          value={project_link}
        />
      </div>
     
      <div className="form-group">
        <input
          onChange={handleChange("github_link")}
          type="number"
          className="form-control"
          placeholder="github"
          value={github_link}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("tech_stack")}
          type="number"
          className="form-control"
          placeholder="tech"
          value={tech_stack}
        />
      </div>
      <button
        type="submit"
        onClick={onSubmit}
        className="btn btn-outline-success mb-3"
      >
        Create project
      </button>
    </form>
  );

  return (
    <Base
      title="Add a Project here!"
      description="Welcome to Project creation section"
      className="container bg-info p-4"
    >
      <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-3">
        Admin Home
      </Link>
      <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {createProjectForm()}
        </div>
      </div>
    </Base>
  );
};

export default AddProject;
